'use strict';

module.exports = function (grunt) {

    grunt.loadNpmTasks('grunt-merge-json');

    grunt.registerMultiTask(
        'cloudfile_to_vocab', 
        'Takes vocab data from cloud spreadsheets and renders it into local json files.', 
        function () {
  
        var options = this.options({
            document_url:          '',
            output_directory:      '',
            google_spreadsheet_id: null,
            worksheets:            [],
            whitelisted_services:  [],
            username:              null,
            password:              null
        });

        var cloudfile_to_vocab = require('./lib/cloudfile_to_vocab')();

        cloudfile_to_vocab.process(options, this.async());
    });
};